definition.script:
node {   
    stage('Auth Schema Migration') {  
        git url: 'https://github.com/hms-dbmi/pic-sure-auth-microapp'
        docker.image('dbmi/pic-sure-db-migrations:pic-sure-db-migration_v1.0').inside('--network=host -v /usr/local/docker-config/flyway/auth:/opt/flyway-migrations/auth "--entrypoint=" ') { 
            sh "rm -rf /opt/flyway-migrations/auth/sql"
            sh "cp -R ./pic-sure-auth-db/db/sql /opt/flyway-migrations/auth/sql"
            sh "/opt/flyway/flyway -X -configFiles=/opt/flyway-migrations/auth/flyway-auth.conf migrate"
            sleep(time:10,unit:"SECONDS") 
        }
    }
    stage('Picsure Schema Migration') {    
        git url: 'https://github.com/hms-dbmi/pic-sure'
        docker.image('dbmi/pic-sure-db-migrations:pic-sure-db-migration_v1.0').inside('--network=host -v /usr/local/docker-config/flyway/picsure:/opt/flyway-migrations/picsure "--entrypoint=" ') { 
            sh "rm -rf /opt/flyway-migrations/picsure/sql"
            sh "cp -R ./pic-sure-api-data/src/main/resources/db/sql /opt/flyway-migrations/picsure/sql"
            sh "/opt/flyway/flyway -X -configFiles=/opt/flyway-migrations/picsure/flyway-picsure.conf migrate"
            sleep(time:10,unit:"SECONDS") 
        }
    }
    stage('PIC-SURE Application Intialization'){
        sh """
            export appplication_uuid=`cat /usr/local/docker-config/httpd/picsureui_settings.json | grep applicationIdForBaseQuery | cut -d ':' -f 2| sed 's/[",]*//g'`
            docker run --network=host -i -v /root/.my.cnf:/root/.my.cnf mysql mysql -h 172.17.0.1 -u root -e "INSERT INTO application VALUES (0x\${appplication_uuid},'PIC-SURE multiple data access API',0x01,'PICSURE','','/picsureui');" auth
        """
    }
    stage('Clean Workspace'){ 
        cleanWs() 
    }

}
