definition.script:
/**
 * This pipeline should simply swap between existing data in the environment.
 **/

pipeline {
   agent any

   stages {
        stage('Swappung data sets') {
            steps{
                script {
                    def result = build job: 'Swap Data Sets', parameters: []
                }
                script {
                    def result = build job: 'Restart HPDS', parameters: []
                } 
            }
        }
   }
}
